package assignment;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		
		System.out.println("Before Swap:");
		System.out.println("a =" +a); 
		System.out.println("b =" +b); 
		a=a+b;//a=1200
		b=a-b;//b=30
		a=a-b;//a=40
	
		
		System.out.println("After Swap:");
		System.out.println("a =" +a); 
		System.out.println("b =" +b); 

	}

}
