package typecasting;

public class Downcasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         byte b=127;
         int i=b;
         System.out.println(i);
         float f=i;
         System.out.println(f);
         Double d=(double)i;
         System.out.println(d);
	}

}


