package typecasting;

public class Downcasting1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double d=15.12;
		int a=124;
		double d1=(double) a;
		System.out.println("Double"+d1);
		int a2=(int)d1;
		double g1=(double)a;
		System.out.println("Doublr ans"+g1);
		System.out.println("int" +d1);

	}

}
